mapper.select:
SELECT r.id, u.name AS user_name, u.phone AS user_phone, r.media_title, r.media_type,
r.media_description, r.start_time, r.end_time, r.total_time, r.store_address AS location
FROM media_browse_record r
LEFT JOIN user u ON u.id = r.user_id

    1 = 1

        AND r.user_id = #{user_id}


        AND r.start_time >= #{start_time}


        AND r.end_time <= #{end_time}


ORDER BY r.created_at DESC
