mapper.select:
SELECT s.id, c.name AS class_name, s.name, s.parent_name, s.parent_phone, s.improved, c.study_progress, cr.title AS currentCourse, s.avatar_url, s.created_at, s.created_by, s.updated_at, s.updated_by, s.left_vision, s.right_vision, fse.left_vision AS first_left_vision, fse.right_vision AS first_right_vision FROM student s LEFT JOIN class c ON s.class_id = c.id LEFT JOIN (SELECT * FROM class_course where id IN (SELECT max(id) FROM class_course GROUP BY class_id)) cc ON s.class_id = cc.class_id LEFT JOIN course cr ON cc.course_id = cr.id LEFT JOIN (SELECT student_id, left_vision, right_vision FROM student_eyesight WHERE id IN( SELECT MIN(id) FROM student_eyesight GROUP BY student_id )) fse ON fse.student_id = s.id
1 = 1
AND s.id = #{id}
AND s.name LIKE #{name}
AND s.store_id = #{store_id}
AND s.class_id = #{class_id}
AND s.rehab_training
AND !s.rehab_training
AND (s.name LIKE #{key_word} OR s.parent_name LIKE #{key_word} OR s.parent_phone LIKE #{key_word})
ORDER BY s.created_at desc
